mapper.select:
SELECT * FROM project_parameter WHERE 1=1
AND NO = #{projNo}
AND 0=1
AND PARAMETER_ID = #{id}
AND 0=1

mapper.delete:
DELETE FROM project_parameter WHERE 1=1
AND NO = #{projNo}
AND 0=1
AND PARAMETER_ID = #{id}
AND 0=1

mapper.update:
UPDATE project_parameter
SET 
SOURCE_VALUE=#{sourceValue},
PARAMETER=#{parameter},
IS_DISPLAY=#{isDisplay}
WHERE NO = #{no} AND PARAMETER_ID = #{parameterId}
