mapper.select:
select ID,NAME,NO , SOURCE,UNIT,IS_DISPLAY AS ISDISPLAY, SMALL_TYPE AS small_type_value, BIG_TYPE AS big_type_value, MONTH as MONTH ,VALUE AS VALUE from ( SELECT PRO.PARAMETER_ID AS ID,PAR.NAME,PRO.NO, (SELECT CODE_MASTER_INFO.NAME FROM CODE_MASTER_INFO WHERE CODE_MASTER_INFO.CODEKEY='Source' AND PRO.SOURCE_VALUE = CODE_MASTER_INFO.VALUE) AS SOURCE, PRO.UNIT AS UNIT, PRO.IS_DISPLAY AS IS_DISPLAY, (SELECT CODE_MASTER_INFO.NAME FROM CODE_MASTER_INFO WHERE CODE_MASTER_INFO.CODEKEY='BigType' AND CODE_MASTER_INFO.VALUE = PAR.BIG_TYPE_VALUE) AS BIG_TYPE, (SELECT CODE_MASTER_INFO.NAME FROM CODE_MASTER_INFO WHERE CODE_MASTER_INFO.CODEKEY='SmallType' AND CODE_MASTER_INFO.VALUE = PAR.SMALL_TYPE_VALUE) AS SMALL_TYPE, MAX(VAL.VALUE) AS VALUE,IFNULL(VAL.MONTH,#{projectParameterValue.yearMonth}) AS MONTH FROM PROJECT_PARAMETER PRO LEFT JOIN PARAMETER_INFO PAR ON PAR.ID = PRO.PARAMETER_ID LEFT JOIN PROJECT_PARAMETER_VALUE_NEW VAL ON VAL.NO = PRO.NO AND VAL.PARAMETER_ID = PRO.PARAMETER_ID AND DATE_FORMAT(VAL.MONTH,'%Y%m') = DATE_FORMAT(#{projectParameterValue.yearMonth},'%Y%m') WHERE PRO.NO = #{projectParameterValue.no} GROUP BY PRO.PARAMETER_ID,PAR.NAME,PRO.SOURCE_VALUE,PAR.BIG_TYPE_VALUE,PAR.SMALL_TYPE_VALUE,PRO.IS_DISPLAY,DATE_FORMAT(VAL.MONTH,'%Y%m') ) b where 1=1
AND NAME LIKE CONCAT('%',#{projectParameterValue.name},'%')
AND BIG_TYPE = #{projectParameterValue.big_type_value}
AND SMALL_TYPE = #{projectParameterValue.small_type_value}
AND SOURCE = #{projectParameterValue.source}
AND UNIT like CONCAT('%',#{projectParameterValue.unit},'%') ESCAPE '/'
AND IS_DISPLAY = #{projectParameterValue.isDisplay}
ORDER BY ${sort} ${order}

select *
from project_parameter_value_new
where no = #{paraValue.no,jdbcType=VARCHAR}
and DATE_FORMAT(MONTH,'%Y%m') = DATE_FORMAT(#{paraValue.month},'%Y%m') and parameter_id = #{paraValue.parameterId}

mapper.update:
UPDATE project_parameter_value_new SET VALUE=#{paramValue}
WHERE NO in(
select NO
from project_info
where version = #{c_version} )AND DATE_FORMAT(MONTH,'%Y%m%d') = #{month} AND
PARAMETER_ID = #{paramId}

UPDATE project_parameter_value_new SET VALUE=#{value}
WHERE NO in(
select NO
from project_info
where version = #{version} ) AND MONTH= #{month} AND
PARAMETER_ID = #{id}
